				ОтменитьТранзакцию();
				Продолжить;
			КонецПопытки;

			Попытка
				Приемник.Записать(?(ПроводитьРеализацию, РежимЗаписиДокумента.Проведение, РежимЗаписиДокумента.Запись));

				ВидДокументаПриемник = Перечисления.ЭКОМ_ВидыДокументов.Накладная_Исходящая;													
