row.Value:
//ВыполнитьГибкиеНастройки(Приемник, , , ЛогСобытий);

// Новый вариант для использования при создании ЭКОМ_Документа
Если Приемник.xmlВалюта = "UAH" Тогда
    Валюта = Справочники.Валюты.НайтиПоКоду(980);
ИначеЕсли Приемник.xmlВалюта = "RUB" Тогда
    Валюта = Справочники.Валюты.НайтиПоКоду(643);
ИначеЕсли Приемник.xmlВалюта = "USD" Тогда
    Валюта = Справочники.Валюты.НайтиПоКоду(840);
ИначеЕсли Приемник.xmlВалюта = "EUR" Тогда
    Валюта = Справочники.Валюты.НайтиПоКоду(978);
ИначеЕсли Приемник.xmlВалюта = "MDL" Тогда
    Валюта = Справочники.Валюты.НайтиПоКоду(498);
ИначеЕсли Приемник.xmlВалюта = "BYR" Тогда
    Валюта = Справочники.Валюты.НайтиПоКоду(974);
ИначеЕсли Приемник.xmlВалюта = "KZT" Тогда
    Валюта = Справочники.Валюты.НайтиПоКоду(398);
Иначе
    Валюта = Справочники.Валюты.НайтиПоКоду(643);
КонецЕсли;

Приемник.Валюта = Валюта;

Если ЗначениеЗаполнено(Приемник.НакладнаяНомер) Тогда
    НомерРТУ = Приемник.НакладнаяНомер;
Иначе
    НомерРТУ = Приемник.ФайлНомер;
КонецЕсли;    
// Заполнение КоличествоПоставляемое из Реализации, если она указана в ЭКОМ документе и DELIVEREDQUANTITY от покупателя не заполнено.
ЗапросРТУ = Новый Запрос;
ЗапросРТУ.Текст = "ВЫБРАТЬ
                  |	РеализацияТоваровУслугТовары.Номенклатура КАК Номенклатура,
                  |	РеализацияТоваровУслугТовары.Количество КАК Количество
                  |ИЗ
                  |	Документ.РеализацияТоваровУслуг.Товары КАК РеализацияТоваровУслугТовары
                  |ГДЕ
                  |	ВЫБОР
                  |			КОГДА ТИПЗНАЧЕНИЯ(&Реализация) = ТИП(Документ.РеализацияТоваровУслуг)
                  |				ТОГДА РеализацияТоваровУслугТовары.Ссылка В (&Реализация)
                  |			ИНАЧЕ РеализацияТоваровУслугТовары.Ссылка.Номер ПОДОБНО &Номер
                  |					И РеализацияТоваровУслугТовары.Ссылка.Дата >= ДОБАВИТЬКДАТЕ(&Дата, ДЕНЬ, -14)
                  |		КОНЕЦ
                  |
                  |УПОРЯДОЧИТЬ ПО
                  |	РеализацияТоваровУслугТовары.Ссылка.Дата УБЫВ";
ЗапросРТУ.УстановитьПараметр("Реализация", ?(Приемник.ДокументыСвязанные.Количество() = 0, Неопределено, Приемник.ДокументыСвязанные.ВыгрузитьКолонку("ДокументСвязанный")));
ЗапросРТУ.УстановитьПараметр("Номер", "%" + НомерРТУ);
ЗапросРТУ.УстановитьПараметр("Дата", ТекущаяДата());

РезультатРТУ = ЗапросРТУ.Выполнить().Выгрузить();
РезультатРТУ.Свернуть("Номенклатура", "Количество");

Для Каждого СтрокаТоваров Из Приемник.ТЧ_Товары Цикл
    Если СтрокаТоваров.КоличествоПоставляемое = 0 Тогда
        СтрокаИзРТУ = РезультатРТУ.Найти(СтрокаТоваров.Номенклатура, "Номенклатура");
        Если НЕ СтрокаИзРТУ = Неопределено Тогда
            СтрокаТоваров.КоличествоПоставляемое = СтрокаИзРТУ.Количество;
        КонецЕсли;
    КонецЕсли;
КонецЦикла;

Приемник.Проведен = Истина;
