Для Каждого Строка Из Результат Цикл

	Строка.DELIVEREDQUANTITY = Формат(Строка.Количество, "ЧГ=0");
	Строка.POSWEIGHTNETTO = Формат(Строка.ВесНеттоШтуки, "ЧДЦ=3; ЧРД=.; ЧГ=0");
	Строка.POSWEIGHTBRUTTO = Формат(Строка.ВесБруттоШтуки, "ЧДЦ=3; ЧРД=.; ЧГ=0");
	Строка.WEIGHTNETTO = Формат(Строка.ВесНеттоПалеты, "ЧДЦ=3; ЧРД=.; ЧГ=0");
	Строка.WEIGHTBRUTTO = Формат(Строка.ВесБруттоПалеты, "ЧДЦ=3; ЧРД=.; ЧГ=0");
	Строка.HEIGHT = Формат(Строка.Высота, "ЧГ=0");
	Строка.VOLUME = Формат(Строка.Объем, "ЧГ=0");
	Строка.HIERARCHICALID = Строка.НомерПалета;

	Строка.PRODUCTIDBUYER = Строка.КодНоменклатурыКонтрагента;

	Если ЗначениеЗаполнено(Строка.КоличествоЗаказ) Тогда
		Строка.ORDEREDQUANTITY 	= Формат(Строка.КоличествоЗаказ, "ЧГ=0");  
		Строка.ORDEREDUNIT     	= ПолучитьЕдиницуИзмерения(Строка.ЕдиницаИзмеренияНаименование);  
	Иначе	
		Строка.ORDEREDQUANTITY 	= Формат(Строка.Количество, "ЧГ=0");
		Строка.ORDEREDUNIT     	= ПолучитьЕдиницуИзмерения(Строка.ЕдиницаИзмеренияНаименование);
	КонецЕсли;																								

	Строка.DELIVEREDQUANTITY 	= Строка.Количество;
	Строка.INVOICEDQUANTITY 	= Строка.Количество;
	Строка.DELIVEREDUNIT        = ПолучитьЕдиницуИзмерения(Строка.ЕдиницаИзмеренияНаименование);
	Строка.INVOICEUNIT          = ПолучитьЕдиницуИзмерения(Строка.ЕдиницаИзмеренияНаименование);

	СуммаБезНДС = Строка.Сумма - ?(Строка.СуммаВключаетНДС, Строка.СуммаНДС, 0);
	СуммаСНДС = Строка.Сумма + ?(Строка.СуммаВключаетНДС, 0, Строка.СуммаНДС);
	Если НЕ Строка.Количество = 0 Тогда
		ЦенаБезНДС  = СуммаБезНДС / Строка.Количество;
		ЦенаСНДС  = СуммаСНДС / Строка.Количество;
	Иначе
		ЦенаБезНДС = 0;
		ЦенаСНДС = 0;
	КонецЕсли;		

	Строка.UNITPRICE     = Формат(ЦенаБезНДС, "ЧДЦ=2; ЧРД=.; ЧГ=0");
	Строка.AMOUNT        = СуммаБезНДС;
	Строка.AMOUNTWITHVAT = Формат(СуммаСНДС, "ЧДЦ=2; ЧРД=.; ЧГ=0");
	Строка.TAXRATE       = СтавкаНДСЧислом(Строка.СтавкаНДС);
	Строка.TAXAMOUNT     = Формат(Строка.СуммаНДС, "ЧДЦ=2; ЧРД=.; ЧН=0; ЧГ=0");
	Строка.PRICE         = Формат(ЦенаБезНДС, "ЧДЦ=2; ЧРД=.; ЧН=0; ЧГ=0");

	Строка.CALIBRE         = Строка.КалибрУпаковки;
	Строка.PACKINGTYPE     = Строка.ТипФасовки;
	Строка.PACKINGQUANTITY = Формат(Строка.КоличествоВФасовке, "ЧДЦ=2; ЧРД=.; ЧГ=0");
	Строка.PACKINGUNIT     = Строка.БазоваяЕдиницаТовара;

	Строка.EXPIREDATE      = Формат(Строка.ДатаОкончанияСрокаГодности, "ДФ=yyyy-MM-dd");
	Строка.MANUFACTUREDATE = Формат(Строка.ДатаПроизводства, "ДФ=yyyy-MM-dd");
	Строка.SHELFLIFEDATE   = Формат(Строка.ДатаПроизводства, "ДФ=yyyy-MM-dd");
	Строка.TEMPERATUREOFSTORAGE = Строка.ТемператураХранения;
	Если ЗначениеЗаполнено(Строка.СрокХраненияВДнях) Тогда
		Строка.VARBESTDATE = Формат(Строка.СрокХраненияВДнях, "ЧДЦ=0; ЧГ=0");
	Иначе
		СрокХраненияВДнях = Цел((Строка.ДатаОкончанияСрокаГодности - Строка.ДатаПроизводства) / 86400 );
		Строка.VARBESTDATE = Формат(СрокХраненияВДнях, "ЧДЦ=0; ЧГ=0");
	КонецЕсли;

КонецЦикла;

Возврат Результат;

