EDIINTERCHANGEID = 0;

МассивСтрокДляУдаления = Новый Массив;
Для Каждого Строка Из Результат Цикл

	Если НЕ Строка.ЕстьПалеты Тогда
		МассивСтрокДляУдаления.Добавить(Строка);
		ТекстОшибки = "DESSCC по документу " + Строка.ПредставлениеРТУ + " не отправлен, т.к. товары не распределены по палетам.";
		ЗаписатьЛогСобытий(ЛогСобытий, ТекстОшибки);
		Продолжить;
	КонецЕсли;

	Строка.FRESH = "0";  // 1 - Да 0 - Нет
	Если ТипЗнч(Строка.СтруктураФайлаORDER) = Тип("ХранилищеЗначения")  Тогда
	     СтруктураФайла = Строка.СтруктураФайлаORDER.Получить().СтруктураФайла;
		 Если СтруктураФайла.Свойство("FRESH") = Истина Тогда
		      Строка.FRESH = СтруктураФайла.FRESH; 
		 КонецЕсли;
	КонецЕсли;

	EDIINTERCHANGEID = НовыйНомерТранзакции(EDIINTERCHANGEID);

	Строка.TRANSPORTID     = СокрЛП(Строка.НомерТС);
	Строка.TRANSPORTERNAME = СокрЛП(Строка.ВодительФамилия) + " " + СокрЛП(Строка.ВодительИмя) + " " + СокрЛП(Строка.ВодительОтчество);
	Строка.NUMBER 		= Эком_ПолучитьНомерНаПечать(Строка.Ссылка);
	Строка.TTNNUMBER    = Эком_ПолучитьНомерНаПечать(Строка.Ссылка);
	Строка.DATE 		= Формат(Строка.Дата,"ДФ=""гггг-ММ-дд""");
	Строка.DELIVERYDATE = Формат(Строка.Дата,"ДФ=""гггг-ММ-дд""");
	Строка.DELIVERYTIME = Формат(Строка.Дата,"ДЛФ=T");
	Строка.ORDERNUMBER 	= Строка.ЗаказНомер;
	Строка.ORDERDATE 	= Формат(Строка.ЗаказДата,"ДФ=""гггг-ММ-дд""");
	Строка.EDIINTERCHANGEID = Формат(EDIINTERCHANGEID, "ЧГ=0");
    Строка.EDIMESSAGE 	 = Формат(ТекущаяДата(), "ДФ=""ггггММддЧЧммсс""") + "-OUT-" +  Формат(EDIINTERCHANGEID, "ЧГ=0");
	Строка.ФайлИмя 		 = "DESSCC" + "_" + Строка.EDIMESSAGE + ".xml";
	Строка.Идентификатор = "DESSCC" + "_" + Строка.EDIMESSAGE;

