|////////////////////////////////////////////////////////////////////////////////
|ВЫБРАТЬ
|	ЭКОМ_НоменклатураКонтрагентов.Номенклатура
|ИЗ
|	НакладнаяТовары КАК НакладнаяТовары
|		ВНУТРЕННЕЕ СОЕДИНЕНИЕ РегистрСведений.ЭКОМ_НоменклатураКонтрагентов КАК ЭКОМ_НоменклатураКонтрагентов
|		ПО НакладнаяТовары.Контрагент = ЭКОМ_НоменклатураКонтрагентов.Контрагент
|			И НакладнаяТовары.Номенклатура = ЭКОМ_НоменклатураКонтрагентов.Номенклатура
|			И (ЭКОМ_НоменклатураКонтрагентов.Маркировка <> """")
|			И (ЭКОМ_НоменклатураКонтрагентов.Маркировка <> ""нет"")
|			И (ЭКОМ_НоменклатураКонтрагентов.GTIN <> """")";
ЗапросМаркировка.УстановитьПараметр("Источник", Источник);
ЕстьМаркированныеТовары = Не ЗапросМаркировка.Выполнить().Пустой(); 

Выборка = РезультатЗапроса.Выбрать();  

Пока Выборка.Следующий() Цикл

	НоваяПозиция = Результат.Добавить();

	НоваяПозиция.Ссылка = Выборка.Ссылка;

	НоваяПозиция.НомСтр = Формат(Выборка.НомерСтроки, "ЧГ=0");
	НоваяПозиция.НаимТов = СокрЛП(Выборка.НаименованиеНоменклатуры);

	НоваяПозиция.КодТов = Выборка.ШтрихкодНоменклатурыКонтрагента;

	КодПроисх   = ВРег(СокрЛП(Выборка.КодПроисх));
	КодПроисх   = ?(ПустаяСтрока(КодПроисх), "643", КодПроисх);
	НоваяПозиция.КодПроисх = КодПроисх;

	КрНаимСтрПр = ВРег(СокрЛП(Выборка.КрНаимСтрПр));
	КрНаимСтрПр = ?(ПустаяСтрока(КрНаимСтрПр), "RU", КрНаимСтрПр);
	НоваяПозиция.КрНаимСтрПр = КрНаимСтрПр;

	Если НЕ КрНаимСтрПр = "RU" Тогда
		НоваяПозиция.НомГТД = Выборка.НомерГТД;
	КонецЕсли;

	Если Выборка.СтавкаНДС = Перечисления.СтавкиНДС.БезНДС Тогда
		НалСт = "без НДС";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС10 Тогда
		НалСт = "10%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС10_110 Тогда
		НалСт = "10/110";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС18 Тогда
		НалСт = "18%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС18_118 Тогда
		НалСт = "18/118";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС20 Тогда
		НалСт = "20%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС20_120 Тогда
		НалСт = "20/120";
	Иначе
		НалСт = "";
	КонецЕсли;

	НоваяПозиция.НалСт = НалСт;

	НоваяПозиция.ОКЕИ_Тов = СокрЛП(Выборка.ОКЕИ_Тов);  //Может ли ОКЕИ корректироваться?
	НоваяПозиция.КолТов = Формат(Выборка.Количество, "ЧДЦ=3; ЧРД=.; ЧГ=0; ЧН=");

	// Учитываем скидки (опционально)
	//Если ЗначениеЗаполнено(Выборка.ОбщаяСкидка) И Выборка.ЭтоКорректировка Тогда
	//	знЦена            = Окр(Выборка.Цена - Выборка.Цена * (Выборка.ОбщаяСкидка / 100), 2);
	//	знЦенаДоИзменения = Окр(Выборка.ЦенаДоИзменения - Выборка.ЦенаДоИзменения * (Выборка.ОбщаяСкидка / 100), 2);
	//	Сумма             = Окр(Выборка.Сумма - Выборка.Сумма * (Выборка.ОбщаяСкидка / 100), 2);
	//	СуммаНДС          = Окр(Выборка.СуммаНДС - Выборка.СуммаНДС * (Выборка.ОбщаяСкидка / 100), 2);
	//ИначеЕсли ЗначениеЗаполнено(Выборка.ОбщаяСкидка) Тогда
	//	знЦена            = Окр(Выборка.Цена - Выборка.Цена * (Выборка.ОбщаяСкидка / 100), 2);
	//	знЦенаДоИзменения = Окр(Выборка.ЦенаДоИзменения - Выборка.ЦенаДоИзменения * (Выборка.ОбщаяСкидка / 100), 2);
	//КонецЕсли;

	Если Выборка.СуммаВключаетНДС Тогда
		СтТовБезНДС = Выборка.Сумма - Выборка.СуммаНДС;
		ЦенаБезНДС  = Окр((Выборка.Цена * 100)/(100 + СтавкаНДСЧислом(Выборка.СтавкаНДС)), 2);
		СтТовУчНал = Выборка.Сумма;
	Иначе
		СтТовБезНДС = Выборка.Сумма;
		ЦенаБезНДС = Выборка.Цена;
		СтТовУчНал = Выборка.Сумма + Выборка.СуммаНДС;
	КонецЕсли;

	НоваяПозиция.СтТовБезНДС = Формат(СтТовБезНДС, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	НоваяПозиция.СтТовУчНал = Формат(СтТовУчНал, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");

	Если НЕ Выборка.СтавкаНДС = Перечисления.СтавкиНДС.БезНДС Тогда
		НоваяПозиция.СумНал = Формат(Выборка.СуммаНДС, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе
		НоваяПозиция.СумНалБезНДС = "без НДС";
	КонецЕсли;

	НоваяПозиция.ЦенаТов = Формат(ЦенаБезНДС, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");

	НоваяПозиция.НаимЕдИзм = СокрЛП(Выборка.НаименованиеЕдиницыИзмерения);
	НоваяПозиция.ПрТовРаб = "1";
	НоваяПозиция.АкцизБезАкциз = "без акциза";

	// Данные для ИнфПолФХЖ2
	ЭКОМ_ИнфоПоле       = Выборка.ЭКОМ_ИнфоПоля;
	НомерНакладной      = СокрЛП(Выборка.НомерРеализации);
	КодТовараПоставщика = СокрЛП(Выборка.КодНоменклатурыПоставщика);
	КодТовараПокупателя = СокрЛП(Выборка.КодНоменклатурыКонтрагента);
	Штрихкод 			= СокрЛП(Выборка.ШтрихкодНоменклатурыКонтрагента);

	ДанныеИнфополя = Новый Соответствие;
	ДанныеИнфоПоля.Вставить("CAMPAIGNNUMBER"               , Выборка.CAMPAIGNNUMBER);
	ДанныеИнфоПоля.Вставить("дата_накладной"               , Выборка.ДатаДокументаОснования);
	ДанныеИнфоПоля.Вставить("номер_уведомления_об_отгрузке", НомерНакладной);
	ДанныеИнфоПоля.Вставить("номер_накладной"              , НомерНакладной);
	ДанныеИнфоПоля.Вставить("номер_отгрузки"               , НомерНакладной);
	ДанныеИнфоПоля.Вставить("номер_акта"                   , Выборка.НомерRECADV);
	ДанныеИнфоПоля.Вставить("дата_акта"                    , Выборка.ДатаRECADV);
	ДанныеИнфоПоля.Вставить("номер_заказа"                 , Выборка.ЗаказНомер);
	ДанныеИнфоПоля.Вставить("дата_заказа"                  , Выборка.ЗаказДата);
	ДанныеИнфоПоля.Вставить("код_материала"                , КодТовараПокупателя);
	ДанныеИнфоПоля.Вставить("код_материала_заказчика"      , КодТовараПокупателя);
	ДанныеИнфоПоля.Вставить("штрихкод"                     , Штрихкод);
	ДанныеИнфоПоля.Вставить("артикул_поставщика"           , КодТовараПоставщика);
	ДанныеИнфоПоля.Вставить("вложенность_кол"              , Выборка.КоличествоВУпаковке);
	ДанныеИнфоПоля.Вставить("вложенность_океи"             , Выборка.Информация);
    ДанныеИнфоПоля.Вставить("позиция_заказа"               , Строка(Выборка.НомерСтроки));
	ДанныеИнфоПоля.Вставить("код_продавца"                 , КодТовараПоставщика);
    ДанныеИнфоПоля.Вставить("КдВЕРНЫЙ"                     , КодТовараПокупателя);
	ДанныеИнфоПоля.Вставить("SKU"                          , КодТовараПокупателя);
	ДанныеИнфоПоля.Вставить("Штрих-код"                    , Штрихкод);

	СоответсвияИнфоПолей = ПолучитьСтруктуруИнфоПолейИзСправочника("ИнфПолФХЖ2", ЭКОМ_ИнфоПоле, ДанныеИнфополя, Выборка.Контрагент_ЭСФБезИнфПоля, ЛогСобытий);

	Если СоответсвияИнфоПолей = Неопределено Тогда
		ТекстЛогаСобытий = "Не заполнены обязательные инфо поля <<<ИнфПолФХЖ2>>>! Формат ЮЗД - " + Выборка.ФорматУПД;
		ЗаписатьЛогСобытий(ЛогСобытий, ТекстЛогаСобытий);

		Результат.Удалить(НоваяПозиция);
		Отказ = Истина;
		Продолжить;

	КонецЕсли;

	НоваяПозиция.ИнфПолСтр = СоответсвияИнфоПолей;

	// Выборка по маркировке
	ДанныеМаркировки = Новый Массив;
	Если Выборка.ЭтоМаркируемыйЮЗД = Истина Тогда
		ЗапросМаркировки = Новый Запрос;
		ЗапросМаркировки.Текст =
		"ВЫБРАТЬ
		|	ЭКОМ_Маркировка.ИдентТрансУпак КАК ИдентТрансУпак,
		|	ЭКОМ_Маркировка.КИЗ КАК КИЗ,
		|	ЭКОМ_Маркировка.НомУпак КАК НомУпак
		|ИЗ
		|	РегистрСведений.ЭКОМ_Маркировка КАК ЭКОМ_Маркировка
		|ГДЕ
		|	ЭКОМ_Маркировка.Номенклатура = &Номенклатура
		|	И ЭКОМ_Маркировка.Накладная = &Накладная
		|
		|УПОРЯДОЧИТЬ ПО
		|	ИдентТрансУпак
		|ИТОГИ ПО
		|	ИдентТрансУпак";
		ЗапросМаркировки.УстановитьПараметр("Номенклатура", Выборка.Номенклатура);
		ЗапросМаркировки.УстановитьПараметр("Накладная"   , Выборка.РеализацияТоваровУслуг);
		РезультатЗапросаМаркировки = ЗапросМаркировки.Выполнить();

		ВыборкаИдентТрансУпак = РезультатЗапросаМаркировки.Выбрать(ОбходРезультатаЗапроса.ПоГруппировкам, "ИдентТрансУпак");
		Пока ВыборкаИдентТрансУпак.Следующий() Цикл
			ЗначенияКодов = Новый Массив;
			СтруктураМаркировки = Новый Структура;
			СтруктураМаркировки.Вставить("ИдентТрансУпак", ВыборкаИдентТрансУпак.ИдентТрансУпак);
			СтруктураМаркировки.Вставить("ЗначенияКодов" , ЗначенияКодов);

			ВыборкаМаркировка = ВыборкаИдентТрансУпак.Выбрать();
			Пока ВыборкаМаркировка.Следующий() Цикл

				ДобавитьКод    = Ложь;
				СтруктураКодов = Новый Структура;
				СтруктураКодов.Вставить("КИЗ"           , "");
				СтруктураКодов.Вставить("НомУпак"       , "");

				Если ЗначениеЗаполнено(ВыборкаМаркировка.КИЗ) Тогда
					СтруктураКодов.Вставить("КИЗ" , ВыборкаМаркировка.КИЗ);
					ДобавитьКод = Истина;
				КонецЕсли;
				Если ЗначениеЗаполнено(ВыборкаМаркировка.НомУпак) Тогда
					СтруктураКодов.Вставить("НомУпак" , ВыборкаМаркировка.НомУпак);
					ДобавитьКод = Истина;
				КонецЕсли;
				Если ДобавитьКод Тогда
					ЗначенияКодов.Добавить(СтруктураКодов);
				КонецЕсли;

			КонецЦикла;
			СтруктураМаркировки.Вставить("ЗначенияКодов" , ЗначенияКодов);

			ДанныеМаркировки.Добавить(СтруктураМаркировки);
		КонецЦикла;
	ИначеЕсли ЕстьМаркированныеТовары И Не ПустаяСтрока(Выборка.GTIN) Тогда   
		ЗначенияКодов = Новый Массив;

		СтруктураКодов = Новый Структура("НомУпак", "02" + Выборка.GTIN + "37" + Выборка.Количество);
		СтруктураКодов.Вставить("ЗначенияКодов" , Новый Структура);   		
		ЗначенияКодов.Добавить(СтруктураКодов);        

		СтруктураМаркировки = Новый Структура;
		СтруктураМаркировки.Вставить("ЗначенияКодов" , ЗначенияКодов);


		ДанныеМаркировки.Добавить(СтруктураМаркировки);
	КонецЕсли;

	НоваяПозиция.ДанныеПоМаркировке = ДанныеМаркировки;

КонецЦикла;

Возврат Результат;

ИспользоватьСтрокуДерева = Истина;
Если СведТов.ИнфПолСтр.Количество() = 0 Тогда
    ИспользоватьСтрокуДерева = Ложь;
КонецЕсли;

Возврат ИспользоватьСтрокуДерева;

